import React, { useState } from "react";

export default function CurrencyInput({ value, onChange }) {
  const [input, setInput] = useState(() => {
    const number = value / 100;
    return number.toLocaleString("pt-br", {
      style: "currency",
      currency: "BRL"
    });
  });

  function maskInput(value) {
    const number = Number(value.replace(/\D/g, "")) / 100;
    return number.toLocaleString("pt-br", {
      style: "currency",
      currency: "BRL"
    });
  }

  function cleanMask(value) {
    return Number(value.replace(/\D/g, "")) / 100;
  }

  function handleChange(e) {
    const result = maskInput(e.target.value);
    setInput(result);
    onChange(result, cleanMask(result));
  }

  return <input type="text" value={input} onChange={handleChange} />;
}
